class CountdownTimer{constructor(){this.timerElements={days:document.querySelector(".main__timer_wrap .timer__inner:nth-child(1) .timer"),hours:document.querySelector(".main__timer_wrap .timer__inner:nth-child(2) .timer"),minutes:document.querySelector(".main__timer_wrap .timer__inner:nth-child(3) .timer"),seconds:document.querySelector(".main__timer_wrap .timer__inner:nth-child(4) .timer")};this.endTime=this.getEndTime()||this.calculateEndTime({days:0,hours:23,minutes:59,seconds:59}),this.timeLeft=this.calculateTimeLeft()}start(){this.updateDisplay(),this.timer=setInterval((()=>this.tick()),1e3)}tick(){this.timeLeft=this.calculateTimeLeft(),this.timeLeft.total<=0&&(clearInterval(this.timer),this.timeLeft={days:0,hours:0,minutes:0,seconds:0}),this.updateDisplay(),this.saveEndTime()}calculateTimeLeft(){const t=this.endTime-(new Date).getTime();return{total:t,days:Math.floor(t/864e5),hours:Math.floor(t/36e5%24),minutes:Math.floor(t/1e3/60%60),seconds:Math.floor(t/1e3%60)}}updateDisplay(){for(const[t,e]of Object.entries(this.timerElements))e&&(e.textContent=this.padZero(this.timeLeft[t]))}padZero(t){return t.toString().padStart(2,"0")}saveEndTime(){localStorage.setItem("countdownTimerEnd",this.endTime.toString())}getEndTime(){const t=localStorage.getItem("countdownTimerEnd");return t?parseInt(t):null}calculateEndTime(t){return(new Date).getTime()+1e3*(24*t.days*60*60+60*t.hours*60+60*t.minutes+t.seconds)}}document.addEventListener("DOMContentLoaded",(()=>{(new CountdownTimer).start()}));import"./countdown-timer.js";